import { Injectable } from '@angular/core';
import { Http } from '@angular/http';
import { ScreenModel, ScreenModelsMap } from '../types';

@Injectable()
export class ScreenModelService {
  private screenModels: Promise<ScreenModelsMap>;

  constructor(
    http: Http
  ) {
    this.screenModels = http.get('screenModels.json')
      .map(res => res.json())
      .toPromise();
  }

  async getById(screenId: string): Promise<ScreenModel> {
    let screenModels = await this.screenModels;
    return screenModels[screenId];
  }

}
